import React, {Component} from "react";

class NFC extends Component {

  render() {
    return (
      <div id="NFC">

        <div className="heading">
            <h1>NFC connection</h1>
        </div>

        <div className="body">
            <h2>Let's try out the NFC functionality</h2>
            <btn type="button" id="scanButton"> Scan Test </btn>
            //<btn type="button" onClick={this._cancel}> Cancel </btn>
        </div>


    </div>
    );
  }
}

  document.getElementById('scanButton').addEventListener("click", async () => {
    console.log("User clicked scan button");

    try {
      const reader = new NDEFReader();
      await reader.scan();
      console.log("> Scan started");

      reader.addEventListener("error", (event) => {
        console.log(`Argh! ${event.message}`);
      });

      reader.addEventListener("reading", ({ message, serialNumber }) => {
        console.log(`> Serial Number: ${serialNumber}`);
        console.log(`> Records: (${message.records.length})`);
      });
    } catch (error) {
      console.log("Argh! " + error);
    }
  });


export default NFC;
