import React, {Component} from "react";
import ReactDOM from 'react-dom';
import Nfc from 'nfc-react-web';

class NFC extends Component {

  render() {
    return (
      <div id="NFC">

        <div className="heading">
            <h1>NFC connection</h1>
        </div>

        <div className="body">
            <h2>Let's try out the NFC functionality</h2>
            <btn type="button"
                 onClick={()=> ReactDOM.render(
                   <Nfc  read={records => { alert(records[0].data);  }} />, document.getElementById('root') )}
                 >Send some info to the tag</btn>
            <btn type="button">Send some info to the tag</btn>
        </div>


    </div>
    );
  }
}

export default NFC;
